/**
 * Conventions:
 * _message: is for unformatted text that will be shown as-is to
 * the user.
 * _text: is for text that will appear in the UI, possibly with
 * decorations, e.g., $(x) uses the https://octicons.github.com/ and should not
 * be localized
 *
 * If ommitted, we will assume _message.
 */
export declare const messages: {
    config_name_text: string;
    select_exception_text: string;
    select_break_option_text: string;
    always_break_text: string;
    never_break_text: string;
    language_client_not_ready: string;
    isv_debug_config_environment_error: string;
};
